import React, { PureComponent } from 'react';
// import { injectIntl } from 'react-intl';
// import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';


const styles = theme => ({
  bebe: {
    fontSize: 32,
  },
  root: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    minHeight: '100vh',
    backgroundColor: '#F7F7F7',
    textAlign: 'center',
    flexDirection: 'column',
  },
  paper: {
    margin: theme.spacing(4),
    padding: `${theme.spacing(3)}px ${theme.spacing(4)}px ${theme.spacing(4)}px`,
    width: '100%',
    maxWidth: theme.leftDrawerWidth,
  },
  contentLogo: {
    width: '100%',
    maxWidth: theme.leftDrawerWidth,
  },
  logo: {
    width: '100%',
    display: 'block',
    margin: `0 auto ${theme.spacing(1)}px`,
  },
});

class Clock extends PureComponent {

  render() {
    const {
      classes,
    } = this.props;

    return (
      <div className={classes.root}>
        <div className={classes.contentLogo}>
          <img className={classes.logo} src="/img/clock.jpg" alt="Clock--ie" />
        </div>
        <Paper>
          <section>
          <Button
            className={isCounting ? "button button--stop" : "button button--start"}
            onClick={onStartBtnClick}
          >
            {isCounting ? 'STOP' : (total != 0 ? 'CONTINUE' : 'START')}
          </Button>

        <Button className="button" onClick={onClearBtnClick}>
          CLEAR
        </Button>
          </section>
          <Typography> 00: 00.00</Typography>
        </Paper>
      </div>
    );
  }
}

