import React from 'react';
import PropTypes from 'prop-types';
import { Route, Redirect } from 'react-router-dom';
import ReactGA from 'react-ga';
import { withStyles } from '@material-ui/core/styles';
import WithMainNav from './with-main-nav';



const styles = theme => ({
  container: {
    minHeight: '100vh',
    position: 'relative',
  },
  footer: {
    position: 'absolute',
    textAlign: 'center',
    backgroundColor: '#f7f7f7',
    bottom: '-100px',
    [theme.breakpoints.down('sm')]: {
      width: '100%',
      marginLeft: '0',
    },
    [theme.breakpoints.up('md')]: {
      width: `calc(100% - ${theme.leftDrawerWidth}px)`,
      marginLeft: theme.leftDrawerWidth,
    },
  },
  partner: {
    width: '10%',
    minWidth: '150px',
    maxWidth: '200px',
  },
});


/* eslint-disable react/destructuring-assignment */
const WrappedRoute = ({
  path,
  exact,
  component: Component,
  mainNav,
  classes,
  adminOnly,
  ...props
}) => {
  // if (adminOnly && (!props.profile.roles || !props.profile.roles.admin)) {
  //   return <Redirect to="/" />;
  // }

  return (
    <Route
      exact={!!exact}
      path={path}
      render={(routeProps) => {
        // Google Analytics tracking...
        // const { pathname, search } = routeProps.location;
        // const page = pathname + search;
        // ReactGA.set({ page });
        // ReactGA.pageview(page);

        return (
          <div className={classes.container}>
            {mainNav
              ? <WithMainNav component={Component} {...props} {...routeProps} />
              : <Component {...props} {...routeProps} />}
          </div>
        );
      }}
    />
  );
};


WrappedRoute.propTypes = {
  path: PropTypes.string.isRequired,
  exact: PropTypes.bool,
  component: PropTypes.func.isRequired,
  mainNav: PropTypes.bool,
  adminOnly: PropTypes.bool,
  classes: PropTypes.shape({
    container: PropTypes.string.isRequired,
    footer: PropTypes.string.isRequired,
    partner: PropTypes.string.isRequired,
  }).isRequired,
  profile: PropTypes.shape({
    roles: PropTypes.shape({
      admin: PropTypes.bool,
    }),
  }).isRequired,
};


WrappedRoute.defaultProps = {
  exact: false,
  mainNav: true,
  adminOnly: false,
};


export default withStyles(styles)(WrappedRoute);
