import React from 'react';
// import { injectIntl } from 'react-intl';
// import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';


const styles = theme => ({
  bebe: {
    fontSize: 32,
  },
  root: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    minHeight: '100vh',
    backgroundColor: '#F7F7F7',
    textAlign: 'center',
    flexDirection: 'column',
  },
  paper: {
    margin: theme.spacing(4),
    padding: `${theme.spacing(3)}px ${theme.spacing(4)}px ${theme.spacing(4)}px`,
    width: '100%',
    maxWidth: theme.leftDrawerWidth,
  },
  contentLogo: {
    width: '100%',
    maxWidth: theme.leftDrawerWidth,
  },
  logo: {
    width: '100%',
    display: 'block',
    margin: `0 auto ${theme.spacing(1)}px`,
  },
});

function padNumber(value) {
  return value > 9 ? String(value) : "0" + value;
}

function StopwatchDisplay({ time }) {
  const h = padNumber(Math.floor(time / 3600000));
  const m = padNumber(Math.floor(time / 60000) % 60);
  const s = padNumber(Math.floor(time / 1000) % 60);
  const c = padNumber(Math.round(time / 10) % 100);
  return (
    <p>
      {h}:{m}:{s}.<small>{c}</small>
    </p>
  );
}

function StopwatchControls({ onToggle }) {
  return (
    <p>
      <button onClick={onToggle}>Start / Stop</button>
    </p>
  );
}

class Clock extends React.Component {

  render() {
    const {
      classes,
    } = this.props;

    return (
      <div className={classes.root}>
        <div className={classes.contentLogo}>
          <img className={classes.logo} src="/img/clock.jpg" alt="Clock--ie" />
        </div>
        <Paper>
          <div className="timer">00 ms</div>
          <StopwatchDisplay time={this.state.time} />
        <StopwatchControls onToggle={this.toggle} />
        </Paper>
      </div>
    );
  }
}

// Clock.propTypes = {

// };


export default withStyles(styles)(Clock);
