import React from 'react';
// import { injectIntl } from 'react-intl';
// import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// import Typography from '@material-ui/core/Typography';
import { Link } from 'react-router-dom';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Fab from '@material-ui/core/Fab';
import ListSubheader from '@material-ui/core/ListSubheader';
import List from '@material-ui/core/List';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Chip from '@material-ui/core/Chip';
import WatchLaterRoundedIcon from '@material-ui/icons/WatchLaterRounded';

import TimelineIcon from '@material-ui/icons/Timeline';
import { Typography } from '@material-ui/core';

import WatchList from '../components/watch-list';
import sortByTime from '../utils/sort';

const styles = theme => ({
  bebe: {
    fontSize: 32,
  },
  root: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    minHeight: '100vh',
    backgroundColor: '#333',
    textAlign: 'center',
    flexDirection: 'column',
  },
  paper: {
    margin: theme.spacing(4),
    padding: `${theme.spacing(3)}px ${theme.spacing(4)}px ${theme.spacing(4)}px`,
    width: '100%',
    maxWidth: theme.leftDrawerWidth,
  },
  contentLogo: {
    width: '100%',
    maxWidth: theme.leftDrawerWidth,
  },
  logo: {
    width: '100%',
    display: 'block',
    margin: `0 auto ${theme.spacing(1)}px`,
  },
});

function padNumber(value) {
  return value > 9 ? String(value) : "0" + value;
}

const getTime = ({ time }) => {
  const hours = padNumber(Math.floor(time / 3600000));
  const minutes = padNumber(Math.floor(time / 60000) % 60);
  const seconds = padNumber(Math.floor(time / 1000) % 60); // Tip: 1000 ms = 1 second
  const c = padNumber(Math.round(time / 10) % 100);
  return (
    <p style={{ margin: 0 }}>
      {hours}:{minutes}:{seconds}.<small>{c}</small>
    </p>
  );
};



function StopwatchDisplay({ time }) {
  const hours = padNumber(Math.floor(time / 3600000));
  const minutes = padNumber(Math.floor(time / 60000) % 60);
  const seconds = padNumber(Math.floor(time / 1000) % 60); // Tip: 1000 ms = 1 second
  const c = padNumber(Math.round(time / 10) % 100);
  return (
    <p className="time">
      {hours}:{minutes}:{seconds}.<small>{c}</small>
    </p>
  );
}

function StopwatchControls({ onToggle, running}) {
  return (
    <Fab onClick={onToggle}>
      {running ? 'STOP' : 'START'}
    </Fab>
  );
}

const bestTime = (attempts) => {
  const best = attempts.sort(sortByTime);
  return best.length === 0 ? '00:00:00': getTime(best[0]);
}

const AverageTime = (attempts) => {
  const average = attempts.reduce((acc, current) => acc + current.time, 0) / attempts.length;
  return getTime({ time: average });
}


class Clock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      time: 0,
      running: false,
      date: '',
    };
    this.toggle = this.toggle.bind(this);
    this.tick = this.tick.bind(this);
  }
  toggle() {
    if (!this.state.running) {
      this.startTime = +new Date();
      this.tick();
    } else {
      const dataLocal = JSON.parse(localStorage.getItem('attempts'));
      if (dataLocal){
        dataLocal.push({ time: this.state.time, date: new Date() })
        localStorage.setItem('attempts', JSON.stringify(dataLocal));
      } else{
        localStorage.setItem('attempts', JSON.stringify([{ time: this.state.time, date: new Date() }]))
      }
      this.update(false);
      this.setState({ time: 0 });
      clearTimeout(this.timeout);
    }
  }
  tick() {
    this.update(true);
    this.timeout = setTimeout(this.tick);
  }
  update(running) {
    this.setState({
      time: +new Date() - this.startTime,
      running
    });
  }
  render() {
    const { classes } = this.props;
    const attempts = JSON.parse(localStorage.getItem('attempts')) || [];
    return (
      <div className={classes.root}>
        <div className="stopwatch">
          <StopwatchDisplay time={this.state.time} />
          <StopwatchControls onToggle={this.toggle} running={this.state.running} />
          <Paper>
            <List
              component="nav"
              aria-labelledby="nested-list-subheader"
              subheader={
                <ListSubheader component="div" id="nested-list-subheader">
                  vueltas
                </ListSubheader>
              }
            >
              {WatchList(attempts)}
            </List>
          </Paper>
          <Paper>
            <Link to={`/history`}>
                <Chip
                  icon={<TimelineIcon />}
                  label="Historial"
                  clickable
                  color="primary"
                  variant="outlined"
                />
              </Link>
            <>
              <Card>
                <CardContent className={classes.content}>
                  <Typography component="h5" variant="h5">
                    Best Time
                  </Typography>
                  <Typography variant="subtitle1" color="textSecondary">
                    {bestTime(attempts)}
                  </Typography>
                </CardContent>
              </Card>
              <Card>
                <CardContent className={classes.content}>
                  <Typography component="h5" variant="h5">
                    Average Time
                  </Typography>
                  <Typography variant="subtitle1" color="textSecondary">
                    {/* {AverageTime(attempts)} */}
                  </Typography>
                </CardContent>
              </Card>
            </>
          </Paper>
        </div>
      </div>
    );
  }
}

// Clock.propTypes = {

// };


export default withStyles(styles)(Clock);
