import React from 'react';
// import { injectIntl } from 'react-intl';
// import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
// import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import Fab from '@material-ui/core/Fab';
import ListSubheader from '@material-ui/core/ListSubheader';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction'
import TimerTwoToneIcon from '@material-ui/icons/TimerTwoTone';
import IconButton from '@material-ui/core/IconButton';
import DeleteForeverTwoToneIcon from '@material-ui/icons/DeleteForeverTwoTone';
import StarRateRoundedIcon from '@material-ui/icons/StarRateRounded';
import Chip from '@material-ui/core/Chip';
import WatchLaterRoundedIcon from '@material-ui/icons/WatchLaterRounded';
import TimelineIcon from '@material-ui/icons/Timeline';
import { Typography } from '@material-ui/core';

const styles = theme => ({
  bebe: {
    fontSize: 32,
  },
  root: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    minHeight: '100vh',
    backgroundColor: '#F7F7F7',
    textAlign: 'center',
    flexDirection: 'column',
  },
  paper: {
    margin: theme.spacing(4),
    padding: `${theme.spacing(3)}px ${theme.spacing(4)}px ${theme.spacing(4)}px`,
    width: '100%',
    maxWidth: theme.leftDrawerWidth,
  },
  contentLogo: {
    width: '100%',
    maxWidth: theme.leftDrawerWidth,
  },
  logo: {
    width: '100%',
    display: 'block',
    margin: `0 auto ${theme.spacing(1)}px`,
  },
});

function padNumber(value) {
  return value > 9 ? String(value) : "0" + value;
}

const getTime = ({ time }) => {
  const hours = padNumber(Math.floor(time / 3600000));
  const minutes = padNumber(Math.floor(time / 60000) % 60);
  const seconds = padNumber(Math.floor(time / 1000) % 60); // Tip: 1000 ms = 1 second
  const c = padNumber(Math.round(time / 10) % 100);
  // return `${hours}:${minutes}:${seconds}.<small>${c}</small>`
  return (
    <p style={{ margin: 0 }}>
      {hours}:{minutes}:{seconds}.<small>{c}</small>
    </p>
  );
};

const WatchList = (intentos) => {
  console.log('Entra a la lista');
  return intentos.map((elem, idx) => (
    <ListItem>
      <ListItemText primary={`Vuelta ${idx +  1 }`}  secondary={getTime(elem)} />
      <ListItemSecondaryAction>
        <IconButton edge="end" aria-label="delete">
          <DeleteForeverTwoToneIcon />
        </IconButton>
      </ListItemSecondaryAction>
    </ListItem>
))}

function StopwatchDisplay({ time }) {
  const hours = padNumber(Math.floor(time / 3600000));
  const minutes = padNumber(Math.floor(time / 60000) % 60);
  const seconds = padNumber(Math.floor(time / 1000) % 60); // Tip: 1000 ms = 1 second
  const c = padNumber(Math.round(time / 10) % 100);
  return (
    <p className="time">
      {hours}:{minutes}:{seconds}.<small>{c}</small>
    </p>
  );
}

function StopwatchControls({ onToggle, running}) {
  return (
    <Fab variant="extended" onClick={onToggle}>
      {running ? 'STOP' : 'START'}
    </Fab>
  );
}

const sortByTime = (a, b) => {
  if (a.time < b.time) {
    return 1;
  }
  if (a.time > b.time) {
    return -1;
  }
  return 0;
};

const bestTime = (attempts) => {
  console.log('bestTime', attempts);
  const best = attempts.sort(sortByTime);
  return getTime(best[0].time);
}
// const AverageTime = (attempts) = {

// }

class Clock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      time: 0,
      running: false,
      date: '',
    };
    this.toggle = this.toggle.bind(this);
    this.tick = this.tick.bind(this);
  }
  toggle() {
    if (!this.state.running) {
      this.startTime = +new Date();
      this.tick();
    } else {
      const dataLocal = JSON.parse(localStorage.getItem('attempts'));
      if (dataLocal){
        dataLocal.push({ time: this.state.time, date: new Date() })
        localStorage.setItem('attempts', JSON.stringify(dataLocal));
      } else{
        localStorage.setItem('attempts', JSON.stringify([{ time: this.state.time, date: new Date() }]))
      }
      this.update(false);
      this.setState({ time: 0 });
      clearTimeout(this.timeout);
    }
  }
  tick() {
    this.update(true);
    this.timeout = setTimeout(this.tick);
  }
  update(running) {
    this.setState({
      time: +new Date() - this.startTime,
      running
    });
  }
  render() {
    const { classes } = this.props;
    return (
      <div className={classes.root}>
        <div className="stopwatch">
          <StopwatchDisplay time={this.state.time} />
          <StopwatchControls onToggle={this.toggle} running={this.state.running} />
          <Paper>
            <Button> <TimelineIcon />Historial</Button>
            <List
              component="nav"
              aria-labelledby="nested-list-subheader"
              subheader={
                <ListSubheader component="div" id="nested-list-subheader">
                  vueltas
                </ListSubheader>
              }
            >
              {WatchList(JSON.parse(localStorage.getItem('attempts')) || [] )}
            </List>
          </Paper>
          <Paper>
            <>
              <Chip
                icon={<StarRateRoundedIcon />}
                label="Best Time"
                clickable
                color="primary"
                variant="outlined"
              />
              <Typography>
                {/* 0 00:00:02.31 */}
                {bestTime(JSON.parse(localStorage.getItem('attempts')) || [] )}}
              </Typography>
            </>
            <>
              <Chip
                icon={<WatchLaterRoundedIcon />}
                label="Average Time"
                clickable
                color="primary"
                variant="outlined"
              />
              <Typography> 00:00:02.15</Typography>
            </>
          </Paper>
        </div>
      </div>
    );
  }
}

// Clock.propTypes = {

// };


export default withStyles(styles)(Clock);
